import { Component } from '@angular/core';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.page.html',
  styleUrls: ['./login.page.scss'],
})
export class LoginPage {
  email: string = ''; // Inicializamos como cadena vacía
  password: string = '';
  passwordFieldType: string = 'password'; 
  passwordToggleIcon: string = 'eye-off';
  constructor(public authService: AuthService) {}

  async onSubmit() {
    if (!this.email || !this.password) {
      alert('Please fill in both fields.');
      return;
    }

    try {
      await this.authService.signIn(this.email, this.password);
      alert('Login successful!');
      this.email = '';
      this.password = '';
    } catch (error:any) {
      alert('Login failed: ' + error.message);
    }
  }

  togglePasswordVisibility() { 
    if (this.passwordFieldType === 'password') {
      this.passwordFieldType = 'text'; 
      this.passwordToggleIcon = 'eye'; 
    } else { 
      this.passwordFieldType = 'password'; 
      this.passwordToggleIcon = 'eye-off'; 
    } 
  }
}
<ion-header>
  <ion-toolbar color="dark">
    <ion-title class="ion-text-center">Iniciar Sesión</ion-title>
  </ion-toolbar>
</ion-header>

  <ion-content [fullscreen]="true" class="ion-padding ion-text-center dark-theme background">
    <div class="logo-container">
      <img src="../../../assets/img/logo.png" width="200" class="logo">
    </div>
    <ion-item class="dark-item">
      <ion-label position="stacked" class="ion-text-center">Correo Electrónico</ion-label>
      <ion-input type="email" [(ngModel)]="email" placeholder="Escribe aquí..."></ion-input>
    </ion-item>
    <ion-item class="dark-item">
      <ion-label position="stacked" class="ion-text-center">Contraseña</ion-label>
      <ion-input [type]="passwordFieldType" [(ngModel)]="password"></ion-input>
      <ion-button fill="clear" slot="end" (click)="togglePasswordVisibility()">
        <ion-icon [name]="passwordToggleIcon"></ion-icon>
      </ion-button>
    </ion-item>
    <br>
    <ion-button expand="block" class="login-button">Iniciar Sesión</ion-button>
  </ion-content>
