import { Component } from '@angular/core';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.page.html',
  styleUrls: ['./login.page.scss'],
})
export class LoginPage {
  email: string = ''; // Inicializamos como cadena vacía
  password: string = '';

  constructor(public authService: AuthService) {}

  async onSubmit() {
    if (!this.email || !this.password) {
      alert('Please fill in both fields.');
      return;
    }

    try {
      await this.authService.signIn(this.email, this.password);
      alert('Login successful!');
      this.email = '';
      this.password = '';
    } catch (error:any) {
      alert('Login failed: ' + error.message);
    }
  }
}
<ion-header>
  <ion-toolbar>
    <ion-title>Login</ion-title>
  </ion-toolbar>
</ion-header>


<ion-content [fullscreen]="true" class="ion-padding ion-text-center background">
  <img src="">
  <img src="../../../assets/img/logo.png" width="200">
  <ion-item class="white-text">
    <ion-input label="Correo Electrónico" name="email" type="email" [(ngModel)]="email"
      labelPlacement="stacked" placeholder="Escribe aquí..."></ion-input>
  </ion-item>
  <ion-item>
    <ion-input label="Contraseña" type="password" labelPlacement="stacked"
      [(ngModel)]="password" name="password" required></ion-input>
  </ion-item>
  <br>
  <ion-button>Iniciar Sesión</ion-button>
</ion-content>
