import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-perfil',
  templateUrl: './perfil.page.html',
  styleUrls: ['./perfil.page.scss'],
})
export class PerfilPage implements OnInit {

  // Variable para almacenar los datos del usuario
  userData: any;

  constructor(private authService: AuthService, private router: Router) {
    this.userData = this.authService.userData; // Obtiene los datos del usuario
  }

  cerrarSesion() {
    this.authService.signOut(); // Llama al método de cerrar sesión
    this.router.navigate(['/login']); // Redirige al login
  }
  ngOnInit() {
  }

}
<!--HTML para mostrar el perfil del usuario activo.-->
<app-header titulo="Perfil"></app-header>
<ion-content [fullscreen]="true" class="background">
  <div class="perfil-container ion-text-center">
    <ion-icon name="person-circle-outline" class="profile-icon"></ion-icon>
    <!-- Muestra el nombre y el correo del usuario -->
    <h2 class="username">{{ userData?.nombre || 'Usuario' }}</h2>
    <p class="user-email">{{ userData?.email || 'Correo no disponible' }}</p>

    <!-- Botón para cerrar sesión -->
    <ion-button color="danger" (click)="cerrarSesion()">
      Cerrar sesión
    </ion-button>
  </div>
</ion-content>
